(()=>{const e=document.getElementById("search-location-input"),t=document.getElementById("search-icon");document.getElementById("current-location"),document.getElementById("today-temperature"),t.addEventListener("click",(function(){let t=e.value;fetch(`https://api.openweathermap.org/data/2.5/weather?q=${t}&appid=20f7632ffc2c022654e4093c6947b4f4`,{mode:"cors"}).then((function(e){return e.json()})).then((function(e){console.log(e),e.name;const t=e.main.temp,n=e.main.humidity,o=e.main.feels_like,c=e.visibility,i=e.wind.speed+" "+e.wind.deg+" deg "+e.wind.gust+" gust",a=e.clouds.all,d=e.weather[0].description;console.log(t,n,o,c,i,a,d)})).catch((function(e){console.log("there was a problem with the request"+e)}))}))})();